import Autoplay from "embla-carousel-autoplay";
import useEmblaCarousel from "embla-carousel-react";
import "../Carousel/CarouselStyle.css";

const CardSmCopy = (props) => {
  const { slides, options, src } = props;
  const [emblaRef] = useEmblaCarousel(options, [Autoplay()]);

  return (
    <div>
      <div className="cardSel-box" ref={emblaRef}>
        {slides.map((index) => (
          <a href="/" className="cardSel-slide" key={index}>
            <div className="cardSel-info">
              <div className="cardSel-title">자연도 소금빵</div>
              <div className="cardSel-sub">
                인천광역시 중구 은하수로 10
              </div>
              <div className="cardSel-sub">더테라스프라자 1층</div>
            </div>
            <img src={src} alt="" />
          </a>
        ))}
      </div>
    </div>
  );
};

export default CardSmCopy;
