import React from "react";
import "./FlowerAnimation.css";

const generatePetals = (count) => {
  const petals = [];
  for (let i = 0; i < count; i++) {
    const randomLeft = Math.random() * 100; // ÌôîÎ©¥ Í∞ÄÎ°ú ÏúÑÏπò (0~100%)
    const randomSize = Math.random() * 15 + 50; // ÍΩÉÏûé ÌÅ¨Í∏∞ (20px ~ 35px)
    const randomDuration = Math.random() * 5 + 5; // Ïï†ÎãàÎ©îÏù¥ÏÖò ÏÜçÎèÑ (5Ï¥à ~ 10Ï¥à)
    const randomDelay = Math.random() * 5; // Ïï†ÎãàÎ©îÏù¥ÏÖò ÎîúÎ†àÏù¥ (0Ï¥à ~ 5Ï¥à)

    petals.push(
      <img
        key={i}
        src="res/images/mapMarker.png"
        alt="ÍΩÉÏûé"
        className="flower-petal"
        style={{
          left: `${randomLeft}%`,
          width: `${randomSize}px`,
          animationDuration: `${randomDuration}s`,
          animationDelay: `${randomDelay}s`,
        }}
      />
    );
  }
  return petals;
};

const FlowerAnimation = () => {
  return (
    <div className="flower-container">
      <h1 className="title">ÍΩÉÏûéÏù¥ Ìù©ÎÇ†Î¶¨Îäî ÌÖåÏä§Ìä∏ üå∏</h1>
      {generatePetals(10)} {/* ÍΩÉÏûé 10Í∞ú ÏÉùÏÑ± */}
    </div>
  );
};

export default FlowerAnimation;
