const StoreModal = ({ isOpen, onClose, children }) => {
  if (!isOpen) return null;

  return (
    <div class="modal-container" onClick={onClose}>
      <div class="marker-box" onClick={(e) => e.stopPropagation()}>
        <div class="modal-header">
          <i
            class="fa-solid fa-chevron-left"
            style={{ fontSize: "24px" }}
            onClick={onClose}
          ></i>
          <i
            class="fa-solid fa-xmark"
            style={{ fontSize: "28px" }}
            onClick={onClose}
          ></i>
        </div>
        <div class="modal-title-img">
          <a href="#" class="title-img-shadow">
            <img
              src="frontend\public\res\images\bread_img2.jpg"
              alt=""
            />
          </a>
        </div>
        <div class="modal-title-box">
          <div class="modal-title">
            <div class="modal-title-num">063-445-2772</div>
            <div class="modal-title-name">이성당</div>
            <div class="modal-title-location">
              전북특별자치도 군산시 중앙로 177
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default StoreModal;
