import "../styles/Modal.css";

const StoreModal = ({ isOpen, onClose, children }) => {
  if (!isOpen) return null;

  return (
    <div class="marker-box" onClick={onClose}>
      <div class="header">
        <i
          class="fa-solid fa-chevron-left"
          style="font-size: 24px"
        ></i>
        <i class="fa-solid fa-xmark" style="font-size: 28px"></i>
      </div>
      <div class="title-img">
        <a href="#" class="title-img-shadow">
          <img src="img/bread_img.jpg" alt="" />
        </a>
      </div>
      <div class="title-box">
        <div class="title">
          <div class="title-num">063-445-2772</div>
          <div class="title-name">이성당</div>
          <div class="title-location">
            전북특별자치도 군산시 중앙로 177
          </div>
        </div>
      </div>
    </div>
  );
};

export default StoreModal;
