import { useEffect, useState } from "react";
import Maps from "../components/Maps";
import MapList from "../components/MapList";
import "../styles/MapList.css";

const MapPage = ({ storeList }) => {
  const [selectedItem, setSelectedItem] = useState();
  const [center, setCenter] = useState({
    lat: 37.4919681,
    lng: 126.5813687,
  });
  const [currentLocation, setCurrentLocation] = useState();

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setCenter({ lat: latitude, lng: longitude });
          setCurrentLocation({ lat: latitude, lng: longitude });
        },
        (error) => {
          console.error("현재 위치를 가져올 수 없습니다.", error);
        }
      );
    } else {
      console.error("위치 정보를 지원하지 않는 브라우저입니다.");
    }
  }, []);

  const handleCurrentLocationClick = () => {
    if (currentLocation) {
      setCenter(currentLocation); // 현재 위치로 이동
    }
  };

  // 지도에서 드래그 후 위치를 업데이트
  const handleMapDragEnd = (map) => {
    const newCenter = map.getCenter(); // 새로운 중심 좌표 얻기
    const newLocation = {
      lat: newCenter.getLat(),
      lng: newCenter.getLng(),
    };
    setCenter(newLocation); // center 상태 업데이트
    setCurrentLocation(newLocation); // currentLocation 상태 업데이트
  };

  return (
    <div style={{ display: "flex", position: "relative" }}>
      <MapList
        list={storeList}
        onSelectItem={setSelectedItem}
        setCenter={setCenter}
      />
      <Maps
        list={storeList}
        selectedItem={selectedItem}
        center={center}
        setCenter={setCenter}
        currentLocation={currentLocation}
        onMapDragEnd={handleMapDragEnd} // 드래그 후 위치 변경 시 처리
      />
      <button
        onClick={handleCurrentLocationClick}
        style={{
          position: "absolute",
          bottom: "20px",
          right: "20px",
          zIndex: 1000,
          padding: "10px",
          backgroundColor: "#fff",
          border: "1px solid #ccc",
          borderRadius: "5px",
        }}
      >
        <i className="fa-solid fa-location-crosshairs"></i> 현재
      </button>
    </div>
  );
};

export default MapPage;
