import { useEffect, useState } from "react";
import { Map, MapMarker } from "react-kakao-maps-sdk";

const Maps = ({ list, center, setCenter, currentLocation }) => {
  const [isOverlayOpen, setIsOverlayOpen] = useState(false);

  useEffect(() => {
    console.log("overlay Open", isOverlayOpen);
  }, [isOverlayOpen]);

  return (
    <Map
      center={center}
      style={{ width: "100vw", height: "calc(100vh - 68px)" }}
      level={3}
    >
      {currentLocation && (
        <MapMarker
          position={currentLocation}
          image={{
            src: "https://i.ibb.co/bKKZGTP/Group-43.png",
            size: {
              width: 40,
              height: 40,
            },
          }}
          onClick={() => setIsOverlayOpen(true)}
        />
      )}
      {list.map((store) => (
        <MapMarker
          key={store.id}
          position={{
            lat: store.lat,
            lng: store.lng,
          }}
          image={{
            src: "https://i.ibb.co/7r9XS2w/map-Marker.png",
            size: {
              width: 56,
              height: 52,
            },
            options: {
              offset: {
                x: 28,
                y: 36,
              },
            },
          }}
        />
      ))}
    </Map>
  );
};

export default Maps;
