import { useEffect, useState } from "react";
import { Map } from "react-kakao-maps-sdk"; // 카카오 맵에서 제공하는 Map 컴포넌트를 사용합니다.
import MapList from "../components/MapList";
import "../styles/MapList.css";

const MapPage = ({ storeList }) => {
  const [selectedItem, setSelectedItem] = useState();
  const [center, setCenter] = useState({
    lat: 37.4919681,
    lng: 126.5813687,
  });
  const [currentLocation, setCurrentLocation] = useState();

  useEffect(() => {
    if (currentLocation) {
      console.log("현재 위치 (currentLocation):", currentLocation);
      console.log("지도 중심 (center):", center);
    }
  }, [currentLocation]);

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setCenter({ lat: latitude, lng: longitude });
          setCurrentLocation({ lat: latitude, lng: longitude });
        },
        (error) => {
          console.error("현재 위치를 가져올 수 없습니다.", error);
        }
      );
    } else {
      console.error("위치 정보를 지원하지 않는 브라우저입니다.");
    }
  }, []);

  const handleCurrentLocationClick = () => {
    if (currentLocation) {
      setCenter(currentLocation); // 버튼 클릭 시, currentLocation으로 중심 이동
    }
  };

  const handleMapDragEnd = (map) => {
    const latlng = map.getCenter(); // 지도 드래그 후 새로운 중심 좌표 가져오기
    const newLocation = {
      lat: latlng.getLat(),
      lng: latlng.getLng(),
    };
    setCenter(newLocation); // center 상태 업데이트
    setCurrentLocation(newLocation); // currentLocation 상태 업데이트
  };

  return (
    <div style={{ display: "flex", position: "relative" }}>
      <MapList
        list={storeList}
        onSelectItem={setSelectedItem}
        setCenter={setCenter}
      />
      <Map
        id="map"
        center={center}
        style={{
          width: "100%",
          height: "350px",
        }}
        level={3}
        onDragEnd={(map) => handleMapDragEnd(map)} // onDragEnd 이벤트로 지도 중심 변경
      />
      <button
        onClick={handleCurrentLocationClick}
        style={{
          position: "absolute",
          bottom: "20px",
          right: "20px",
          zIndex: 1000,
          padding: "10px",
          backgroundColor: "#fff",
          border: "1px solid #ccc",
          borderRadius: "5px",
        }}
      >
        <i className="fa-solid fa-location-crosshairs"></i>
      </button>
    </div>
  );
};

export default MapPage;
