import { useEffect, useState } from "react";
import Maps from "../components/Maps";
import MapList from "../components/MapList";
import "../styles/MapList.css";

const MapPage = ({ storeList }) => {
  const [selectedItem, setSelectedItem] = useState();
  const [center, setCenter] = useState({
    lat: 37.4919681,
    lng: 126.5813687,
  });
  const [currentLocation, setCurrentLocation] = useState();

  // 일정 범위 이상 차이날 때 currentLocation을 업데이트
  const isLocationDifferent = () => {
    const distanceThreshold = 0.0005; // 0.0005 degrees 정도 차이날 때 위치 갱신
    const latDiff = Math.abs(center.lat - currentLocation?.lat);
    const lngDiff = Math.abs(center.lng - currentLocation?.lng);
    return latDiff > distanceThreshold || lngDiff > distanceThreshold;
  };

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setCenter({ lat: latitude, lng: longitude });
          setCurrentLocation({ lat: latitude, lng: longitude });
        },
        (error) => {
          console.error("현재 위치를 가져올 수 없습니다.", error);
        }
      );
    } else {
      console.error("위치 정보를 지원하지 않는 브라우저입니다.");
    }
  }, []);

  const handleCurrentLocationClick = () => {
    if (currentLocation) {
      setCenter(currentLocation); // 현재 위치로 이동
    }
  };

  // 화면상 표시된 위치가 내 위치와 일정 차이날 때 currentLocation 업데이트
  useEffect(() => {
    if (currentLocation) {
      // 위치가 변경될 때마다 로그로 center와 currentLocation 확인
      console.log("현재 위치:", currentLocation);
      console.log("지도 중심:", center);
    }

    if (currentLocation && isLocationDifferent()) {
      setCurrentLocation(center); // 현재 지도 중심이 내 위치와 다르면 업데이트
    }
  }, [center, currentLocation]); // center 또는 currentLocation이 변경될 때마다 실행

  return (
    <div style={{ display: "flex", position: "relative" }}>
      <MapList
        list={storeList}
        onSelectItem={setSelectedItem}
        setCenter={setCenter}
      />
      <Maps
        list={storeList}
        selectedItem={selectedItem}
        center={center}
        setCenter={setCenter}
        currentLocation={currentLocation}
      />
      <button
        onClick={handleCurrentLocationClick}
        style={{
          position: "absolute",
          bottom: "20px",
          right: "20px",
          zIndex: 1000,
          padding: "10px",
          backgroundColor: "#fff",
          border: "1px solid #ccc",
          borderRadius: "5px",
        }}
      >
        <i className="fa-solid fa-location-crosshairs"></i>
      </button>
    </div>
  );
};

export default MapPage;
