        <CardSmCopy slides={SLIDES} options={OPTIONS} />
      </div>
      {/* <div className="Carousel">
        <EmblaCarousel
          slides={SLIDES}
          options={OPTIONS}
          src={"/res/images/bread_img.jpg"}
