import { useState } from "react";
import { Map, MapMarker } from "react-kakao-maps-sdk";

const Maps = ({ list, center, setCenter, currentLocation }) => {
  const [data, setData] =
    useState <
    {
      level: number,
      position: {
        lat: number,
        lng: number,
      },
    };
  return (
    <Map
      center={center}
      style={{ width: "100vw", height: "calc(100vh - 68px)" }}
      level={5}
      onCenterChanged={(map) => {
        const level = map.getLevel();
        const latlng = map.getCenter();

        setData({
          level: level,
          position: {
            lat: latlng.getLat(),
            lng: latlng.getLng(),
          },
        });

        console.log(`${latlng.getLat()}, ${latlng.getLng()}`);
      }}
    >
      {currentLocation && (
        <MapMarker
          position={currentLocation}
          image={{
            src: "https://i.ibb.co/bKKZGTP/Group-43.png",
            size: {
              width: 40,
              height: 40,
            },
          }}
        />
      )}
      {list.map((store) => (
        <MapMarker
          key={store.id}
          position={{
            lat: store.lat,
            lng: store.lng,
          }}
          image={{
            src: "https://i.ibb.co/7r9XS2w/map-Marker.png",
            size: {
              width: 56,
              height: 52,
            },
            options: {
              offset: {
                x: 28,
                y: 36,
              },
            },
          }}
        />
      ))}
    </Map>
  );
};

export default Maps;
