    const { offsetTop, offsetLeft, offsetWidth, offsetHeight } = node
    const offset: NodeRectType = {
      top: offsetTop,
      right: offsetLeft + offsetWidth,
      bottom: offsetTop + offsetHeight,
      left: offsetLeft,
      width: offsetWidth,
      height: offsetHeight
    }

    return offset
  }

  const self: NodeRectsType = {
    measure
  }
  return self
}
