import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

// 공통 InputField 컴포넌트
const InputField = ({
  label,
  type,
  name,
  value,
  onChange,
  error,
  placeholder,
  icon,
  toggleVisibility,
  isPassword,
}) => (
  <div className="flex-column">
    <label>{label}</label>
    {error && <span className="error">{error}</span>}
    <div className="inputForm">
      {icon}
      <input
        type={type}
        name={name}
        className="input"
        placeholder={placeholder}
        value={value}
        onChange={onChange}
      />
      {isPassword && (
        <i
          className={`fa-solid ${
            type === "password" ? "fa-eye" : "fa-eye-slash"
          }`}
          style={{ cursor: "pointer" }}
          onClick={toggleVisibility}
          aria-label="Toggle password visibility"
        ></i>
      )}
    </div>
  </div>
);

const SignupForm = () => {
  const [formData, setFormData] = useState({
    nickname: "",
    email: "",
    password: "",
    confirmPassword: "",
  });

  const [errors, setErrors] = useState({});
  const [passwordVisible, setPasswordVisible] = useState(false);
  const [confirmPasswordVisible, setConfirmPasswordVisible] =
    useState(false);

  const navigate = useNavigate();

  const validateForm = () => {
    const newErrors = {};
    if (!formData.nickname)
      newErrors.nickname = "닉네임을 입력해주세요.";

    if (!formData.email || !/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = "유효한 이메일을 입력해주세요.";
    }

    const passwordRegex =
      /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,16}$/;
    if (!formData.password) {
      newErrors.password = "비밀번호를 입력해주세요.";
    } else if (!passwordRegex.test(formData.password)) {
      newErrors.password =
        "영문자, 숫자, 특수기호를 포함해 8~16자리를 입력해주세요.";
    }

    if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = "비밀번호가 일치하지 않습니다.";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      console.log("폼 제출: ", formData);
      setFormData({
        nickname: "",
        email: "",
        password: "",
        confirmPassword: "",
      });
      // Navigate or further actions
    }
  };

  const handleNavigateToLogin = () => {
    navigate("/login");
  };

  return (
    <form className="form" onSubmit={handleSubmit}>
      <InputField
        label="닉네임"
        type="text"
        name="nickname"
        value={formData.nickname}
        onChange={handleChange}
        error={errors.nickname}
        placeholder="사용하실 닉네임을 입력해주세요"
        icon={
          <i
            className="bi bi-person"
            style={{ fontSize: "24px" }}
          ></i>
        }
      />

      <InputField
        label="이메일"
        type="email"
        name="email"
        value={formData.email}
        onChange={handleChange}
        error={errors.email}
        placeholder="이메일을 입력해주세요"
        icon={
          <svg
            height={20}
            viewBox="0 0 32 32"
            width={20}
            xmlns="http://www.w3.org/2000/svg"
          >
            {/* Add SVG paths here */}
          </svg>
        }
      />

      <InputField
        label="비밀번호"
        type={passwordVisible ? "text" : "password"}
        name="password"
        value={formData.password}
        onChange={handleChange}
        error={errors.password}
        placeholder="대소문자 숫자가 포함된 8~16자 이내"
        toggleVisibility={() => setPasswordVisible((prev) => !prev)}
        isPassword
        icon={
          <svg
            height={20}
            viewBox="-64 0 512 512"
            width={20}
            xmlns="http://www.w3.org/2000/svg"
          >
            {/* Add SVG paths here */}
          </svg>
        }
      />

      <InputField
        label="비밀번호 확인"
        type={confirmPasswordVisible ? "text" : "password"}
        name="confirmPassword"
        value={formData.confirmPassword}
        onChange={handleChange}
        error={errors.confirmPassword}
        placeholder="위와 동일한 비밀번호를 입력해주세요"
        toggleVisibility={() =>
          setConfirmPasswordVisible((prev) => !prev)
        }
        isPassword
        icon={
          <svg
            height={20}
            viewBox="-64 0 512 512"
            width={20}
            xmlns="http://www.w3.org/2000/svg"
          >
            {/* Add SVG paths here */}
          </svg>
        }
      />

      <button className="button-submit">회원가입</button>
      <p className="p">
        이미 회원이신가요?{" "}
        <span className="span" onClick={handleNavigateToLogin}>
          로그인하기
        </span>
      </p>
    </form>
  );
};

export default SignupForm;
