import { useState } from "react";
import MapItem from "./MapItem";
import StoreModal from "./StoreModal";

const MapList = ({ list, setCenter }) => {
  const [search, setSearch] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedStore, setSelectedStore] = useState(null);

  const onChangeSearch = (e) => {
    setSearch(e.target.value);
  };

  const getFilteredData = () => {
    if (search === "") {
      return list;
    }
    return list.filter((item) =>
      item.name.toLowerCase().includes(search.toLowerCase())
    );
  };

  const handleOpenModal = (store) => {
    setSelectedStore(store);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedStore(null);
  };

  const filteredList = getFilteredData();

  return (
    <div>
      <div className="lists-box">
        <div className="list-title-box">
          <div className="list-title">
            ÏÇ¨ÎûåÎì§Í≥º Ïù∏ÏÉùÎπµÏßëÏùÑ
            <div></div>
            Í≥µÏú†Ìï¥Î¥êÏöî üëæ
          </div>
        </div>
        <div className="list-header">
          <div className="list-sub-header">
            <div id="sub-title">Ïù∏ÏÉùÎπµÏßë Î¶¨Ïä§Ìä∏</div>
            <div id="sub-btn">
              <button>
                <div className="sub-btn-icon">
                  <i className="fa-solid fa-plus"></i>
                </div>
                ÎÇòÎèÑ Ï∂îÍ∞ÄÌïòÍ∏∞
              </button>
            </div>
          </div>
          <div className="list-search-box">
            <span className="list-search-btn">
              <i className="fa-solid fa-magnifying-glass"></i>{" "}
            </span>
            <input
              value={search}
              onChange={onChangeSearch}
              className="list-search-txt"
              placeholder="Í≤ÄÏÉâ"
            />
          </div>
        </div>
        <div className="items_wrapper">
          {filteredList.map((item) => (
            <MapItem key={item.id} {...item} setCenter={setCenter} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default MapList;
